import React,{useEffect,useState} from 'react'
import {useDispatch,useSelector} from 'react-redux'
import {Row,Col,Form,Button,Card,Image,ListGroup, NavItem} from 'react-bootstrap'
import {Link} from 'react-router-dom'
import { addToCart } from '../actions/cartAction'


const Cart = ({match,location,history}) => {
    const productId=match.params.id
    const qty=location.search? Number(location.search.split("=")[1]) :1;
    const dispatch=useDispatch()
    useEffect(()=>{
        if(productId){
            dispatch(addToCart(productId,qty))
        }
    },[dispatch,qty,productId])

    const cart=useSelector((state)=>state.cart)
    const {cartItems}=cart
    
    
    return (
        <>
          <Row>
              <Col md={8} >
                  <h2>Shopping Cart</h2>
                  {cartItems.length===0?<message>your cart is empty!! <Link to="/">Go back</Link>
                  
                  </message>:(
                    <ListGroup variant="flush">
                        {cartItems.map(item=>(
                            <Row>
                            <Col md={2}>
                                <Image src={item.image}fluid rounded/>
                            </Col>
                            <Col md={3}><Link to={`/products/product/${item.product}`}>{item.title}</Link></Col>
                            <Col md={2}>{item.price}</Col>
                            <Col md={2}><Form.Control
                                    as="select"
                                    value={item.qty}
                                    onChange={(e)=>dispatch(addToCart(item.product,e.target.value))}
                                    >
                                        {[...Array(item.countInStock).keys()].map((num)=>(
                                            <option
                                            style={{color:'black'}} 
                                            key={num+1}
                                            value={num+1}
                                            >
                                                {num+1}
                                            </option>
                                        ))}
                                    </Form.Control></Col>
                        </Row>
                        ))
                      }
                          
                    </ListGroup>
                  )}
                </Col>              
            </Row>  
        </>
    )
}

export default Cart
