import {ORDER_REQUEST,ORDER_SUCCESS,ORDER_FAILURE} from '../constants/orderConstant'


const orderReducer=(state={}, action)=>{
    switch (action.type) {
        case ORDER_REQUEST:
            return {loading:true}
        case ORDER_SUCCESS:
            return {
                loading:false,
                success:true,
                order:action.payload
            }    
        case ORDER_FAILURE:
            return {loading:false, error:action.payload}            
                
        default:
            return state;
    }
}
