import React,{useState, useEffect} from 'react'
//import {Link} from 'react-router-dom'
import {useDispatch, useSelector} from 'react-redux'
import {getUserProfile, login, userLog,userUpdate} from '../actions/userAction'
import {Form, Row, Col, Button,Container} from 'react-bootstrap'
import { USER_UPDATE_FAILURE } from '../constants/userConstant'



const Profile = ({history}) => {

    const [name, setName]=useState('')
    const [email, setEmail]=useState('')
    const [password, setPassword]=useState('')
    const [confirmPassword, setConfirmPassword]=useState('')
    const [message, setMessage]=useState('')
    
    const dispatch=useDispatch()
    const userProfile=useSelector((state)=>state.userProfile)
    const{loading, error, user}=userProfile    
    
    const userLog=useSelector(state=>state.userLog)
    const {userInfo}=userLog
    
    useEffect(()=>{
    if(!userInfo){
        history.push('/login')
    }else{
       if(!user.name){
           dispatch(getUserProfile('profile'))
       }else{
           setName(user.name)
           setEmail(user.email)
       }
    }
    
},[history,user,userInfo,dispatch])

const handleSubmit= (e)=>{
    e.preventDefault()
    dispatch(userUpdate({id:user._id,name, email, password}))
    }
    return (
        <>
            <Container>
                <Row>
                    <Col md={3}>
                    
                    <h2>Edit Profile</h2>
                    {error&&<h2>{error}</h2>}
                    {loading&& <h2>Loading...</h2>}
                    {/* {success&& <h2>Profile updated!!</h2>} */}
            <Form onSubmit={handleSubmit}>
            <Form.Group controlId="name" >
                    <Form.Label>Name </Form.Label>
                    <Form.Control 
                    type="text"
                    placeholder="your name"
                    value={name}
                    onChange={(e)=>setName(e.target.value)}
                    />
                </Form.Group>
                <Form.Group controlId="email" >
                    <Form.Label>Email </Form.Label>
                    <Form.Control 
                    type="email"
                    placeholder="email"
                    value={email}
                    onChange={(e)=>setEmail(e.target.value)}
                    />
                </Form.Group>
                <Form.Group controlId="password" >
                    <Form.Label>Password </Form.Label>
                    <Form.Control 
                    type="password"
                    placeholder="password"
                    value={password}
                    onChange={(e)=>setPassword(e.target.value)}
                    />
                </Form.Group>
                <Form.Group controlId="confirmpassword" >
                    <Form.Label>Re-enter Password </Form.Label>
                    <Form.Control 
                    type="password"
                    placeholder="confirm password"
                    value={confirmPassword}
                    onChange={(e)=>setConfirmPassword(e.target.value)}
                    />
                    {message&&<h5>{message}</h5>}
                </Form.Group>
                <Button type="submit">Update</Button>
            </Form>         
        
                    </Col>
                    <Col md={9}>
                        <h2>My Orders</h2>
                    </Col>
                </Row>
            </Container>
        </>
    )
}

export default Profile
