import { createStore, combineReducers, applyMiddleware } from 'redux';
import thunk from 'redux-thunk';
import { productsReducer, productDetailsReducer } from './reducers/productsReducer';
import { userRegReducer, userLogReducer, userProfileReducer } from './reducers/userReducer';
import { cartReducer } from './reducers/cartReducer';
import { composeWithDevTools } from 'redux-devtools-extension';
const userInfoFromStorage = localStorage.getItem('userInfo') ? JSON.parse(localStorage.getItem('userInfo')) : null;
const cartItemFromStorage = localStorage.getItem("cartItems") ? JSON.parse(localStorage.getItem("cartItems")) : [];
const reducer = combineReducers({
  productList: productsReducer,
  productDetails: productDetailsReducer,
  cart: cartReducer,
  userReg: userRegReducer,
  userLog: userLogReducer,
  userProfile: userProfileReducer
});
const initialState = {
  cart: {
    cartItems: cartItemFromStorage
  },
  //userReg:{userReg:userRegFromStorage},
  userLog: {
    userInfo: userInfoFromStorage
  }
};
const middleware = [thunk];
const store = createStore(reducer, initialState, composeWithDevTools(applyMiddleware(...middleware)));
export default store;
