import React,{useEffect, useState} from 'react'
import {Row, Col, Button, ListGroup, Image, Card} from 'react-bootstrap'
import { PayPalButton } from "react-paypal-button-v2"
import {Link} from 'react-router-dom'
import { getOrderDetails, payOrder } from '../actions/orderAction'
import {ORDER_PAY_RESET} from '../constants/orderConstant'
import {useDispatch,useSelector} from 'react-redux'
import axios from 'axios'


const Order = ({match}) => {
    const [sdkReady, setSdkReady]=useState(false)
    const orderId=match.params.id
    const dispatch = useDispatch()
    const orderDetails=useSelector(state=>state.orderDetails)
    const {order,loading, error}=orderDetails
    const orderPay=useSelector(state=>state.orderPay)
    const {loading:loadingPay, success:successPay}=orderPay
    
    
    useEffect(()=>{
        const addPaypalScript= async ()=>{
            const {data:clientId}= await axios.get('/config/paypal')
            const script=document.createElement('script')
            script.type='text/javascript'
            script.src=`https://www.paypal.com/sdk/js?client-id=${clientId}">`
            script.async=true;
            script.onload=()=>{
                setSdkReady(true)
            }
            document.body.appendChild(script)
        }
        if(!order||successPay){
            dispatch(getOrderDetails(orderId))
            dispatch({type:ORDER_PAY_RESET})
        }else{
            if(!window.paypal){
                addPaypalScript()
            }else{
                setSdkReady(true)
            }
        }
    },[dispatch,orderId, order, successPay])

    const handlePaymentSuccess=(PaymentResult)=>{
        console.log(PaymentResult)
        dispatch(payOrder(orderId, PaymentResult))

    }
    
    return (
        <>
          <h2>Order</h2>  
          <Row>
              <Col md={8}>
              <ListGroup variant="flush">
                    <ListGroup.Item>
                        <h2>Shipping</h2>
                            <p><strong>Adress</strong>
                            {order.shippingAdress.adress}, {order.shippingAdress.city},  {order.shippingAdress.postalcode},  {order.shippingAdress.country}</p>
                    </ListGroup.Item>            
                    <ListGroup.Item>
                        <h2>Payment Method</h2>
                            <p><strong>{order.paymentMethod}</strong></p>
                    </ListGroup.Item>
                </ListGroup>
                    <ListGroup variant="flush">
                        <ListGroup.Item><h2>Order Items</h2></ListGroup.Item>
                         {/* {order.orderItems.length===0 
                        ?(<h3>your Order list is empty!</h3>)
                        :(<ListGroup.Item>
                            {order.orderItems.map((item,index)=>(
                          <ListGroup.Item key={index}>
                              <Row>
                                  <Col md={1}>
                                      <Image src={item.image} alt={item.title} fluid/>
                                  </Col>
                                  <Col>
                                    <Link to={`/products/product/${item.product}`}>{item.title}</Link>
                                  </Col>                                 
                                </Row>
                            </ListGroup.Item>  
                        ))}
                        </ListGroup.Item>)}
                         */}
                    </ListGroup>
              </Col>
              <Col md={4}>
                <ListGroup.Item>
                    <PayPalButton 
                        amount={200}
                        onSuccess={handlePaymentSuccess}
                        />
                </ListGroup.Item> 
                </Col>
          </Row>
        </>
    )
}

export default Order
