import React,{useState,useEffect} from 'react'
import axios from 'axios'
import { Row,Col,Button,Image,ListGroupItem } from 'react-bootstrap'
import Ratings from './Ratings'


const ProductDetails = ({match}) => {
    const [product,setProduct]=useState([])
    useEffect(()=>{
        const fetchProduct=async ()=>{
            const {data}=await axios.get(`/products/product/${match.params._id}`);
            setProduct(data)
        };
        fetchProduct()
    },[])
//    const product=Products[match.params.id-1]
   console.log(product);
    return (
        <div>
            <Row>
            <Col md={6} className="mt-3">
            <Image style={{width:'75%'}} fluid src={product.image} />
            </Col>    
            <Col  md={3}>
                <ListGroupItem className="my-3 text-light">
                    <h3>{product.title}</h3>
                    <Ratings rating={product.rating}/>                    
                </ListGroupItem>
                <ListGroupItem className="text-light">{product.description}</ListGroupItem>
                <ListGroupItem className="text-light">Price: {product.price} $</ListGroupItem>
                </Col>    
            <Col>
            <ListGroupItem className="my-5 text-light">Status: out of Stock</ListGroupItem>
            <ListGroupItem><Button type="button" >Add to Cart</Button></ListGroupItem>
            </Col>    
            </Row> 
        </div>
    )
}

export default ProductDetails
