import axios from 'axios'
import {
    ORDER_REQUEST,
    ORDER_SUCCESS,
    ORDER_FAILURE,
    ORDER_DETAIL_REQUEST,
    ORDER_DETAIL_SUCCESS,
    ORDER_DETAIL_FAILURE} from '../constants/orderConstant'

export const createOrders=(order)=>async (dispatch, getState)=>{
    try {
        dispatch({
            type:ORDER_REQUEST
        })
        const {userLog:{userInfo}}=getState()
        const config= {headers:{
            'Content-Type': 'application/json',
            Authorization:`Bearer ${userInfo.token}`
        }}
        const {data}= await axios.post('/order',order, config)
        dispatch({
            type:ORDER_SUCCESS,
            payload:data
        })
    } catch (error) {
        dispatch({
            type:ORDER_FAILURE,
            payload:error.response && error.response.data.message
            ?error.response.data.message
            :error.message
        })
        
    }
}

export const orderDetailReducer=(state={orderItems:[], loading:true,shippingAdress:{}}, action)=>{
    switch (action.type) {
        case ORDER_DETAIL_REQUEST:
            return {
                ...state,
                loading:true
            }
        case ORDER_DETAIL_SUCCESS:
            return {
                loading:false,
                order:action.payload
            }
        case ORDER_DETAIL_FAILURE:    
            return {
                loading:false,
                error:action.payload
            }
    
        default:
            return state;
    }
}
