import {USER_LOG_REQUEST, USER_LOG_SUCCESS, USER_LOG_FAILURE, USER_LOG_LOGOUT} from '../constants/userConstant'

export const userLogReducer=(state={}, action)=>{

    switch (action.type) {
        case USER_LOG_REQUEST:
           return {
               loading:true
            } 
        case USER_LOG_SUCCESS:
            return {
                loading:false,
                userInfo:action.payload
            }    
        case USER_LOG_FAILURE:
            return {
                loading:false,
                error:action.payload
            }
        case USER_LOG_LOGOUT:
            return {}
        default:
        return    state;
    }
}
